package day2

class Day2{
    companion object {
        @JvmStatic fun main(args: Array<String>){
            val numPAD1 = listOf(
                        listOf(1, 2, 3),
                        listOf(4, 5, 6),
                        listOf(7, 8, 9)
                    );

            val numPAD2 = listOf(
                    listOf(null, null, 1, null, null),
                    listOf(null, 2, 3, 4, null),
                    listOf(5, 6, 7, 8, 9),
                    listOf(null, 'A', 'B', 'C', null),
                    listOf(null, null, 'D', null, null)
            );

            println(getCode(Pair(2, 0), numPAD2));
        }

        private fun getCode(initialPositon: Pair<Int, Int>, numPad: List<List<Any?>>): String {
            var (x, y) = initialPositon;
            var code: String = "";

            getInput().forEach { i ->
                i.forEach { move ->
                    when (move) {
                        'U' -> if (x > 0 && numPad[x - 1][y] != null) x--;
                        'D' -> if (x < numPad.size - 1 && numPad[x + 1][y] != null) x++;
                        'L' -> if (y > 0 && numPad[x][y - 1] != null) y--;
                        'R' -> if (y < numPad.size - 1 && numPad[x][y + 1] != null) y++;
                    }

                }

                code += numPad[x][y];
            }

            return code;
        }

        fun getInput(): List<String>{

            val input = "\n" +
                    "\n" +
                    "\n" +
                    "\n" +
                    "";

            return input.split("\n");
        }
    }
}